INSERT INTO `ape_operate_log` VALUES ('1789514812031062020', '分页获取笔记', 7, 'com.ape.apeadmin.controller.article.ApeArticleController.getApeArticlePage()', 'POST', 1, '1697436716646531073', '超级管理员', 'admin', '2024-05-12 12:35:53', '/article/getApeArticlePage', '127.0.0.1', NULL, '{}', '{\"code\":1000,\"data\":{\"current\":1,\"hitCount\":false,\"optimizeCountSql\":true,\"orders\":[],\"pages\":1,\"records\":[{\"articleDesc\":\"腹有诗书气自华,以良好的心态助人助已,把学到的知识告诉别人,帮助他人理解书籍,吸取精华。在讲书的时,沉着冷静,不慌乱,不在意别人的眼光,战胜自己\",\"avatar\":\"/img/3f23a94094654e9f9405ec7d9b2d5e51.jpeg\",\"content\":\"<p><span style=\\\"background-color: rgb(254, 239, 239); color: rgb(28, 50, 228);\\\">量变引起质变，一旦一个人大量阅读，他就会在阅读中学会耐心和坚持，给自己的心灵一份修炼。然而没有什么事情是一蹴而就的，如果想获得改变就需要不断坚持。朱老师在书中说：“人是由他读的书造就的。你读什么样的书，你就会成为什么样的人。读最好的书，做有根的人。与最好的书对话，就是与最好的人为伍。\\\"作为教育工作者，要保持一颗坦然的心，要淡泊明志，更要耐得住寂寞，学会静心阅读、潜心修炼。读书就意味着坐冷板凳，这是一种锻炼耐力的拉锯战，这是一个读书人的力量，这更是教师职业品质和人格精神的力量。看身边爱好读书的人，你总会轻易发觉，他们有一个淡泊坦然、自在从容的心，理性的逻辑思维和开放的胸怀。</span></p>\",\"createBy\":\"朗威\",\"createTime\":\"2024-05-12 12:35:46\",\"id\":\"1789514780619919361\",\"state\":2,\"taskId\":\"1789493729957556225\",\"taskName\":\"潜心修炼，热爱书籍\",\"title\":\"读最好的书，做有根的人\",\"updateBy\":\"朗威\",\"updateTime\":\"2024-05-12 12:35:46\",\"userId\":\"1729701951310757890\"}],\"searchCount\":true,\"size\":10,\"total\":1},\"message\":\"操作成功!\",\"success\":true}', 0, NULL);
