INSERT INTO `ape_operate_log` VALUES ('1853423290059141127', '分页查询用户', 7, 'com.ape.apeadmin.controller.user.ApeUserController.getUserPage()', 'POST', 1, '1697436716646531073', '超级管理员', 'admin', '2024-11-04 21:05:22', '/user/getUserPage', '127.0.0.1', NULL, '{}', '{\"code\":1000,\"data\":{\"current\":1,\"hitCount\":false,\"optimizeCountSql\":true,\"orders\":[],\"pages\":2,\"records\":[{\"address\":\"山东\",\"age\":24,\"agree\":\"初级讲师\",\"avatar\":\"/img/bea1b295cd0b94ecbf34d7e68d615196.jpeg\",\"birth\":\"2023-11-28\",\"country\":\"中国\",\"createTime\":\"2023-11-29 10:57:21\",\"delFlag\":0,\"email\":\"2573415378@qq.com\",\"flair\":\"/img/1701226553307.jpeg\",\"id\":\"1729696014348910594\",\"loginAccount\":\"rushuang\",\"major\":\"信息技术管理\",\"remark\":\"软件编程语言培训和软件工程师速成培训专家。2001年创办锐信科技有限公司，一直致力于中国的软件开发教育事业，擅长网络应用程序开发和数据库编程。逐步将自己多年的开发与培训经验心得编写成通俗易懂的书籍，并将所有擅长的课程录制成课堂现场视频，为实用编程技术在中国的普及作出力所能及的贡献\",\"school\":\"中国石油大学\",\"sex\":1,\"status\":0,\"tel\":\"1472589631\",\"userName\":\"如霜\",\"workDate\":1},{\"address\":\"山东\",\"age\":27,\"agree\":\"初级讲师\",\"avatar\":\"/img/6eb713e8e0aeeefb1f2215584668d5a5.jpeg\",\"birth\":\"2023-11-19\",\"country\":\"中国\",\"createBy\":\"超级管理员\",\"createTime\":\"2023-11-29 14:11:53\",\"delFlag\":0,\"email\":\"anqiao@qq.com\",\"flair\":\"/img/1701238302090.jpeg\",\"id\":\"25cce9e59d9f0cf22c9479a49f71891d\",\"loginAccount\":\"anqiao\",\"major\":\"大数据分析与治理\",\"remark\":\"高级软件工程师，系统架构师，资深培训讲师。三年的IT行业从业经历，精通J2EE,对Spring,Struts,Hibernate，JQuery,Ajax等开源框架有较深入的研究,并精通Oracle、MSSQL,MySQL等大中型数据库,长期从事教育培训、科研课题研究及参与国内多所重点院校开发实训课程。个人简介职业背景曾在中国电信、东方红集团、中国人保PICC、新东方等多家知名企业担当项目经理、技术经理、软件构架师、研发中心经理等\",\"school\":\"山东师范大学\",\"sex\":1,\"status\":0,\"tel\":\"123456789\",\"userName\":\"安巧\",\"workDate\":3},{\"address\":\"山东\",\"age\":29,\"agree\":\"高级讲师\",\"avatar\":\"/img/3f23a94094654e9f9405ec7d9b2d5e51.jpeg\",\"birth\":\"2023-11-20\",\"country\":\"中国\",\"createBy\":\"超级管理员\",\"createTime\":\"2023-11-29 11:14:37\",\"delFlag\":0,\"email\":\"congnan@qq.com\",\"flair\":\"/im