INSERT INTO `ape_task_favor` VALUES ('1747864861761449985', '1729751427215790082', '769dc998d617bff4e30c4bde94f37e90', 'Java零基础入门', '/img/1701239807968.jpg', '本书是针对Java语言初学者编写的基础教程，包含Java程序设计的基础知识，以及大量实用性很强的编程实例。全书共分11章，包括Java语言概述、Java语言基础、类与对象、继承与接口、数组与字符串、Java的异常处理机制、Java常见类库、输入输出及数据库操作、多线程、Applet程序及应用和图形用户界面设计。');
INSERT INTO `ape_task_favor` VALUES ('1747866183139065858', '1729766176024518658', '769dc998d617bff4e30c4bde94f37e90', 'Spring框架全套教程', '/img/1701243304666.png', 'spring框架是于2003年兴起的一个轻量级的java开发框架,有Rod Johnson创建,是针对bean的生命周期进行管理的轻量级容器,使现有的技术更加容易使用,简单来说Spring就是一个轻量级的控制反转(IOC)和面向切面编程(AOP)的框架 !');
